#include<stdio.h>
#include <unistd.h>

int	ft_str_is_numeric(char	*str)
{
	int i;

	i = 0;
	if (str[i] == '\0')
		return (1);

	while (str[i])
		{
		if(57 >= str[i] && str[i] >= 48)
			return(1);
		i++;
		}
	return(0);
}
int	main()
{
	char dizi[]="";
	printf("%d", ft_str_is_numeric(dizi));
}
